#!/usr/bin/pythonimport osimport sysimport jsonimport randomclass Get_UA:	def __init__(self, device=None, limit=None):		self.max = 501		self.limit = limit		self.device = device.lower()		if self.device and self.limit:			self.findUserAgents()	def findUserAgents(self):		path = os.path.realpath(__file__)		dir = os.path.dirname(path)		try:			with open(f"{dir}/data/get_useragents.json", "r") as f:				data = json.loads(f.read())			self.user_agents = data["g4rzk"][f"{self.device}"]		except:			self.user_agents = ("error data....")	def GetUserAgents(self):		while True:			return self.user_agents			self.count +=1			if self.count >= self.max:				break	def RandomUserAgents(self):		return random.choice(self.user_agents)